import React, {useContext, useState} from 'react';
import Modal from "../UI/Modal";
import CartContext from "../store/cart-context";
import CartItem from './CartItem';
import Checkout from './Checkout';
import classes from "./Cart.module.css";

const Cart = (props) => {
  const cartCxtObj = useContext(CartContext);
  const [isCheckout, setCheckout] = useState(false)
  const removeItemHandler = (id) => {
    cartCxtObj.removeItem(id);
  }
  const addItemHandler = (item) => {
    cartCxtObj.addItem({...item, amount: 1})
  }
  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCxtObj.items.map((item) => (
        <CartItem
        key = {item.id}
        price = {item.price}
        amount = {item.amount}
        onRemove = {removeItemHandler.bind(null, item.id)}
        onAdd = {addItemHandler.bind(null, item)}
        />
      ))}
    </ul>
  );

  const orderHandler = () =>{
    setCheckout(true)
  }
  return (
    <Modal onClick={props.onClick}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>${cartCxtObj.totalAmount.toFixed(2)}</span>
      </div>
      <Checkout onCancel = {props.onClick}/>
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onClick}>
          Close
        </button>
        {cartCxtObj.items.length > 0 && <button className={classes.button} onClick = {orderHandler}>Order</button>}
      </div>
    </Modal>
  );
};

export default Cart;
